import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './ChatApp.css';

function HomePage() {
  const [senderName, setSenderName] = useState('');
  const [recipientName, setRecipientName] = useState('');
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Check if senderName and recipientName are not empty
    if (senderName.trim() !== '' && recipientName.trim() !== '') {
      // Redirect to ChatApp page with senderName and recipientName as URL params
      history.push(`/chat?sender=${senderName}&recipient=${recipientName}`);
    } else {
      alert('Please enter both sender and recipient names.');
    }
  };

  return (
    <div className="home-container">
      <h1>Welcome to Chat App</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="sender">Sender Name:</label>
        <input
          type="text"
          id="sender"
          value={senderName}
          onChange={(e) => setSenderName(e.target.value)}
          required
        />
        <label htmlFor="recipient">Recipient Name:</label>
        <input
          type="text"
          id="recipient"
          value={recipientName}
          onChange={(e) => setRecipientName(e.target.value)}
          required
        />
        <button type="submit">Start Chat</button>
      </form>
    </div>
  );
}

export default HomePage;
