import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import socketIOClient from 'socket.io-client';
import './ChatApp.css'; // Import CSS file for styling

const url = 'http://localhost:3001/api'; // Your backend API base URL
const socket = socketIOClient(url); // Initialize WebSocket client

function ChatApp({ isSender }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const [senderName, setSenderName] = useState('');
  const [recipientName, setRecipientName] = useState('');
  const [recipientNumber, setRecipientNumber] = useState('');
  const [seenMessage, setSeenMessage] = useState(null);
  const messageContainerRef = useRef(null);

  useEffect(() => {
    if (isSender) {
      // Initialize sender-specific data
      setSenderName(localStorage.getItem('senderName'));
      setRecipientName(localStorage.getItem('recipientName'));
      setRecipientNumber(localStorage.getItem('recipientNumber'));
    } else {
      // Initialize recipient-specific data
      setSenderName(localStorage.getItem('recipientName'));
      setRecipientName(localStorage.getItem('senderName'));
      setRecipientNumber(localStorage.getItem('senderNumber'));
    }

    // Fetch messages for both sender and recipient
    fetchMessages();

    if (socket.connected) {
      // Subscribe to WebSocket events
      socket.on('newMessage', (newMessage) => {
        setMessages([...messages, newMessage]);
        if (isSender && newMessage.recipientNumber === recipientNumber) {
          // Pop up message on recipient's phone for sender's messages
          alert(`New message from ${newMessage.senderName}: ${newMessage.text}`);
        }
      });
    }

    return () => {
      // Clean up WebSocket subscription
      socket.off('newMessage');
    };
  }, [socket, isSender, recipientNumber]);

  useEffect(() => {
    // Scroll to the bottom of the message container on new messages
    if (messageContainerRef.current) {
      messageContainerRef.current.scrollTop = messageContainerRef.current.scrollHeight;
    }
  }, [messages]);

  const fetchMessages = async () => {
    try {
      const response = await axios.get(`${url}/messages`);
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const sendMessage = async () => {
    if (text.trim() !== '' && senderName.trim() !== '' && recipientName.trim() !== '' && recipientNumber.trim() !== '') {
      try {
        const response = await axios.post(`${url}/messages`, {
          text,
          senderName,
          recipientName,
          recipientNumber,
        });
        setMessages([...messages, response.data]); // Add the new message to the messages array
        setText(''); // Clear the input box
        if (socket.connected) {
          socket.emit('newMessage', response.data); // Send new message event via WebSocket
        }
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const handleSeenMessage = (messageId) => {
    setSeenMessage(messageId);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSender) {
      // Save sender's data in localStorage
      localStorage.setItem('senderName', e.target.senderName.value);
      localStorage.setItem('recipientName', e.target.recipientName.value);
      localStorage.setItem('recipientNumber', e.target.recipientNumber.value);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>ChatPro</h1>
      </div>
      <div className="chat-screen">
        <div className="message-container" ref={messageContainerRef}>
          {messages && messages.length > 0 ? (
            messages.map((msg) => (
              <div key={msg.id} className={`message ${msg.id === seenMessage ? 'seen' : ''}`}>
                <span className={msg.senderName === senderName ? 'sent' : 'received'}>
                  {msg.senderName === senderName ? 'You: ' : `${msg.senderName}: `}
                </span>
                <span>{msg.text}</span>
                {msg.senderName === senderName && <span className="tick-mark">&#10003;</span>} {/* Display tick mark for sent messages */}
                <button onClick={() => handleSeenMessage(msg.id)}>âœ“</button> {/* Seen indicator */}
              </div>
            ))
          ) : (
            <p>No messages available</p>
          )}
        </div>
        <div className="input-container">
          {isSender ? (
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                name="senderName"
                defaultValue={localStorage.getItem('senderName')}
                hidden
              />
              <input
                type="text"
                name="recipientName"
                defaultValue={localStorage.getItem('recipientName')}
                hidden
              />
              <input
                type="text"
                name="recipientNumber"
                defaultValue={localStorage.getItem('recipientNumber')}
                hidden
              />
              <input
                type="text"
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="Type a message..."
                className="message-input"
              />
              <button onClick={sendMessage} className="send-button">Send</button>
            </form>
          ) : (
            <input
              type="text"
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Type a message..."
              className="message-input"
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default ChatApp;
